                                        'crown_angle' => $Crn_Ag,
                                        'crown_height' => $Crn_Ht,
                                        'pavilion_angle' => $Pav_Ag,
                                        'pavilion_depth' => $Pav_Dp,
                                        'certificate_no' => $certi,
